import { Component, OnInit } from '@angular/core';
import { ShopOwnerService } from './shopowner.service';

@Component({
  selector: 'app-shopowner',
  templateUrl: './shopowner.component.html',
  styleUrls: ['./shopowner.component.css']
})
export class ShopOwnerComponent implements OnInit {
  shopOwners: any[] = []; // Initialize as an empty array to avoid undefined errors
  errorMessage: string = ''; // To store any error messages

  constructor(private shopOwnerService: ShopOwnerService) {}

  ngOnInit(): void {
    this.loadShopOwners();
  }

  loadShopOwners() {
    this.shopOwnerService.getShopOwners().subscribe(
      (data) => {
        this.shopOwners = data;
      },
      (error) => {
        console.error('Error fetching Shop Owners:', error);
        this.errorMessage = 'Failed to load shop owners. Please try again later.';
      }
    );
  }
}
