import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { map } from 'rxjs/operators';  // Import map from rxjs

@Injectable({
  providedIn: 'root'
})
export class ShopOwnerService {
  API_URL = "http://localhost:8066/shopOwners"; // Your API base URL

  constructor(private http: HttpClient) {}

  // Register a new ShopOwner
  public registerShopOwners(shopOwnerData: any) {
    return this.http.post(`${this.API_URL}/shopowners`, shopOwnerData); 
  }

  // Get the list of ShopOwners
  public getShopOwners() {
    return this.http.get<any>(`${this.API_URL}/shopowners`).pipe(
      map((response: any) => {
        // Check if the response contains an array under a property like 'data'
        if (response && response.data && Array.isArray(response.data)) {
          return response.data;  // Extract the array of shop owners
        } else {
          console.error('ShopOwners data is not an array:', response);
          return [];  // Return an empty array if the data is not an array
        }
      })
    );
  }

  // Delete a ShopOwner by ID
  public deleteShopOwners(id: number) {
    return this.http.delete(`${this.API_URL}/shopowners/${id}`); 
  }

  // Update a ShopOwner
  public updateShopOwners(shopOwner: any) { // Renamed the method to avoid conflict
    const id = shopOwner.id || shopOwner.id;
    return this.http.put(`${this.API_URL}/shopowners/${id}`, shopOwner); 
  }
}
