import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class CustomerService {
  API = "http://localhost:8066";  // Update API base URL if needed

  constructor(private http: HttpClient) {}

  // Register a new customer
  public registerCustomer(customerData: any) {
    return this.http.post(`${this.API}/customers`, customerData);  // Adjusted endpoint
  }

  // Get all customers
  public getCustomers() {
    return this.http.get(`${this.API}/customers`);  // Adjusted endpoint
  }

  // Delete a customer by ID
  public deleteCustomer(customerId: number) {
    return this.http.delete(`${this.API}/customers/${customerId}`);  // Adjusted endpoint
  }

  // Update customer details
  public updateCustomer(customer: any) {
    const customerId = customer.customerId;  // Ensure compatibility
    return this.http.put(`${this.API}/customers/${customerId}`, customer);  // Adjusted to match backend endpoint
  }
}
