import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ShopOwnerDetailsService {
  API = "http://localhost:8066";

  constructor(private http: HttpClient) {}

  // Register a new shop owner
  public registerShopOwner(shopOwnerData: any) {
    return this.http.post(`${this.API}/shopownerdetails`, shopOwnerData); // Adjusted to match backend endpoint
  }

  // Get all shop owners
  public getShopOwners() {
    return this.http.get(`${this.API}/shopownerdetails`); // Adjusted to match backend endpoint
  }

  // Delete a shop owner by ID
  public deleteShopOwner(shopOwnerId: number) {
    return this.http.delete(`${this.API}/shopownerdetails/${shopOwnerId}`); // Adjusted to match backend endpoint
  }

  // Update a shop owner
  public updateShopOwner(shopOwner: any) {
    const shopOwnerId = shopOwner.id || shopOwner.shopOwnerId; // Ensure compatibility
    return this.http.put(`${this.API}/shopownerdetails/${shopOwnerId}`, shopOwner); // Adjusted to match backend endpoint
  }
}
