import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ShopOwnerService {
  API = "http://localhost:8066";
  updateShopOwners: any;

  constructor(private http: HttpClient) {}

  // Register a new order
  public registerShopOwners(shopownerData: any) {
    return this.http.post('${this.API}/shopOwners', shopownerData); 
  }


  public getShopOwners() {
    return this.http.get<any>('${this.API}/shopOwners').pipe(
      map((response: any) => {
        if (response && response._embedded && Array.isArray(response._embedded.shopOwners)) {
          return response._embedded.shopOwners; // Return the actual shop owners array
        } else {
          console.error('Unexpected response format:', response);
          return []; // Return an empty array if the structure is unexpected
        }
      })
    );
  }



  public deleteShopOwners(id: number) {

    return this.http.delete('${this.API}/shopOwners/${id}'); 
  }


  public updateShopOwenrs(ShopOwner: any) {
    const id = ShopOwner.id || ShopOwner.id;
    return this.http.put('${this.API}/shopOwners/${id}', ShopOwner); 
  }
}
