import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ShopOwnerService {
  API = "http://localhost:8066"; // Base API URL

  constructor(private http: HttpClient) {}

  /**
   * Register a new shop owner
   * @param shopOwnerData Data of the shop owner to register
   * @returns Observable of the API response
   */
  public registerShopOwner(shopOwnerData: any) {
    return this.http.post(`${this.API}`, shopOwnerData);
  }

  /**
   * Fetch all shop owners
   * @returns Observable containing the list of shop owners
   */
  // ShopOwner.service.ts
public getShopOwners() {
  return this.http.get<any[]>(`${this.API}`); // Specify the return type as an array
}


  /**
   * Delete a shop owner by ID
   * @param id ID of the shop owner to delete
   * @returns Observable of the API response
   */
  public deleteShopOwner(id: number) {
    return this.http.delete(`${this.API}/${id}`);
  }

  /**
   * Update a shop owner's information
   * @param shopOwner Updated shop owner data
   * @returns Observable of the API response
   */
  public updateShopOwner(shopOwner: any) {
    return this.http.put(`${this.API}/${shopOwner.id}`, shopOwner);
  }
}
