    if (!owner.id) {
      console.error('Owner ID is undefined. Cannot delete shop owner.');
      return;
    }
    console.log('Deleting shop owner with ID:', owner.id);
    this.shopOwnerService.deleteShopOwner(owner.id).subscribe(response => {
      console.log('Shop owner deleted:', response);
      this.getShopOwnerDetails();
    }, error => {
      console.error('Error deleting shop owner:', error);
    });
  }
  // Edit a shop owner
  edit(owner) {
    this.shopOwnerToUpdate = {
      ...owner
    };
  }
  // Update a shop owner
  updateShopOwner() {
    this.shopOwnerService.updateShopOwner(this.shopOwnerToUpdate).subscribe(response => {
      console.log('Shop owner updated:', response);
      this.getShopOwnerDetails();
    }, error => {
      console.error('Error updating shop owner:', error);
    });
  }
}
