import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ShopOwnerService {
  API = "http://localhost:8066";

  constructor(private http: HttpClient) {}

  // Register a new Shop Owner
  public registerShopOwners(shopownerData: any) {
    return this.http.post(`${this.API}/shopOwners`, shopownerData); 
  }

  // Get all Shop Owners
  public getShopOwners() {
    return this.http.get<any>(`${this.API}/shopOwners`).pipe(
      map((response: any) => {
        if (response && response._embedded && Array.isArray(response._embedded.shopOwners)) {
          return response._embedded.shopOwners; // Extract the array of shop owners
        } else {
          console.error('Unexpected response format:', response);
          return []; // Return an empty array for unexpected formats
        }
      })
    );
  }

  // Delete a Shop Owner
  public deleteShopOwners(shopOwnerId: number) {
    return this.http.delete(`${this.API}/shopOwners/${shopOwnerId}`); 
  }

  // Update a Shop Owner
  public updateShopOwners(shopOwner: any) {
    if (!shopOwner?.id) {
      console.error('Cannot update Shop Owner. ID is missing.');
      return;
    }
    const shopOwnerId = shopOwner.id;
    return this.http.put(`${this.API}/shopOwners/${shopOwnerId}`, shopOwner); 
  }
}
