import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ShopOwnerService {
  API = "http://localhost:8066";

  constructor(private http: HttpClient) {}

  // Register a new order
  public registerShopOwners(shopownerData: any) {
    return this.http.post(`${this.API}/shopowners`, shopownerData); // Adjusted to match backend endpoint
  }

  // Get all orders
  public getShopOwners() {
    return this.http.get(`${this.API}/shopowners`); // Adjusted to match backend endpoint
  }

  // Delete an order by ID
  public deleteShopOwners(id: number) {

    return this.http.delete(`${this.API}/shopowners/${id}`); // Adjusted to match backend endpoint
  }

  // Update an order
  public updateShopOwenrs(ShopOwner: any) {
    const id = ShopOwner.id || ShopOwner.id; // Ensure compatibility
    return this.http.put(`${this.API}/orderdetails/${id}`, ShopOwner); // Adjusted to match backend endpoint
  }
}
