import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ShopOwnerService {
  API = "http://localhost:8066";

  constructor(private http: HttpClient) {}

  // Register a new Shop Owner
  public registerShopOwners(shopOwnerData: any) {
    return this.http.post(`${this.API}/shopOwners`, shopOwnerData); // Adjusted to match backend endpoint
  }

  // Get all Shop Owners
  public getShopOwners() {
    return this.http.get(`${this.API}/shopOwners`); // Adjusted to match backend endpoint
  }

  // Delete a Shop Owner by ID
  public deleteShopOwners(shopOwnerId: number) {
    return this.http.delete(`${this.API}/shopOwners/${shopOwnerId}`); // Adjusted to match backend endpoint
  }

  // Update a Shop Owner
  public updateShopOwners(shopOwner: any) {
    const shopOwnerId = shopOwner.id || shopOwner.shopOwnerId; // Ensure compatibility
    return this.http.put(`${this.API}/shopOwners/${shopOwnerId}`, shopOwner); // Adjusted to match backend endpoint
  }
}
